import 'package:flutter/material.dart';
import 'package:url_launcher/url_launcher.dart';
import '../models/film.dart';

class FilmDetailsPage extends StatelessWidget {
  final Film film;

  const FilmDetailsPage({Key? key, required this.film}) : super(key: key);

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(
        title: Text(film.title),
        backgroundColor: Colors.white, // App bar'ın arka planı saydam olsun
        elevation: 0, // Gölge efekti kaldırılsın
      ),
      body: SizedBox.expand(
       child: Container(
        decoration: BoxDecoration(
          image: DecorationImage(
            image: const NetworkImage('data:image/jpeg;base64,'), // Replace 'url' with actual background image URL
            colorFilter: ColorFilter.mode(Colors.black.withOpacity(0.5), BlendMode.darken),
            fit: BoxFit.cover, // Apply semi-transparent overlay
          ),
        ),
        child: SingleChildScrollView(
          padding: const EdgeInsets.all(16.0),
          child: Column(
            crossAxisAlignment: CrossAxisAlignment.start,
            children: <Widget>[
              Hero(
                tag: film.title,
                child: Image.network(film.imageUrl),
              ),
              const SizedBox(height: 16.0),
              Text(
                film.title,
                style: const TextStyle(fontSize: 24.0, fontWeight: FontWeight.bold, color: Colors.white), // Removed 'const'
              ),
              const SizedBox(height: 8.0),
              Text(
                '${film.year} - ${film.genre}',
                style: const TextStyle(fontSize: 16.0, fontStyle: FontStyle.italic, color: Colors.white), // Removed 'const'
              ),
              const SizedBox(height: 16.0),
              Text(
                'Director: ${film.director}',
                style: const TextStyle(fontSize: 16.0, color: Colors.white), // Removed 'const'
              ),
              const SizedBox(height: 16.0),
              Text(
                film.summary,
                style: const TextStyle(fontSize: 16.0, color: Colors.white), // Removed 'const'
              ),
              const SizedBox(height: 16.0),
              ElevatedButton(
                onPressed: () async {
                  final url = film.imdbUrl as Uri;
                  if (await canLaunchUrl(url.toString() as Uri)) {
                    await launchUrl(url.toString() as Uri);
                  } else {
                    throw 'Could not launch IMDb URL: $url'; // Provide a more informative error message
                  }
                                },
                child: const Text('View on IMDB'),
              ),
            ],
          ),
        ),
       ),
      )
    );
  }
}
